mapper.update:
UPDATE BOARD_T
SET viewCnt = viewCnt + 1
WHERE id = #{id}

UPDATE BOARD_T
SET title = #{title}
, context = #{context}
, updateDate = SYSDATE
WHERE id = #{id}

mapper.delete:
DELETE FROM BOARD_T
WHERE id IN

	#{id}

mapper.update:
UPDATE BOARD_T
   SET nRecCnt = nRecCnt + 1
 WHERE id = #{id}

UPDATE BOARD_T
   SET nRecCnt = nRecCnt - 1
 WHERE id = #{id}

UPDATE BOARD_T
   SET recCnt = recCnt + 1
 WHERE id = #{id}

UPDATE BOARD_T
   SET recCnt = recCnt - 1
 WHERE id = #{id}
